import React, { useState, useEffect } from 'react';
import axios from 'axios';


const Connections = () => {
  const [connections, setConnections] = useState([]);

  useEffect(() => {
    // Fetch connections from the backend API
    axios.get('localhost:4000/api/connectionuser') // Replace with your API endpoint
      .then(response => {
        setConnections(response.data.connections);
      })
      .catch(error => {
        console.error('Error fetching connections:', error);
      });
  }, []);

  return (
    <div>
      <div className='abc container bg-primary text-white mb-5'>WELCOME TO THE ORU PHONES</div>
      <h1 className='text-center text-white bg-danger py-5' >Connections</h1>
      <ul>
        {connections.map(connection => (
          <div key={connection._id} className='card card-body bg-secondary'>{connection.name} - {connection.email}
                 <div className='btn bg-white text-success'>Connect</div>
                 </div>

        ))}
      </ul>
    </div>
  );
};

export default Connections;
