import React, { useState, useEffect } from 'react';

const Profile = () => {
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    // Fetch data from the backend API
    fetch('http://localhost:4000/api/getuser')
      .then(response => response.json())
      .then(data => {
        setUserData(data.data); // Assuming data.data contains the user data
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div>
        <div className='abc container bg-primary text-white'>WELCOME TO THE ORU PHONES</div>
      <h1 className='text-center mt-5 mb-5 bg-danger text-white py-5'>My Profile</h1>
     <div className='container pqr'>
      <ul>
        {userData.map(user => (
          <div key={user._id}>
                      <div className='card card-body fs-5 ' style={{width:"400px", height: "310px"}}><div className='fw-bold'>Name: </div> 
                      <div>{user.name}</div>
                      <div ><div className='fw-bold my-3'>Phone Number: </div>
                        <div >{user.phone_no}</div></div>
                      <div className='fw-bold my-3'>Address: </div>
                        <div>{user.location}</div></div>

                        {/* ------------------------------------- */}

                       <div className='card card-body fs-5 ' style={{width:"400px", height: "310px"}}><div className='fw-bold'>About us: </div>
                        <div>{user.about}</div></div>


                        {/* ------------------------- */}
                       <div>Phone Number: 
                        <div>{user.phone_no}</div></div>


                   </div>
        ))}
      </ul>
      </div>
    </div>
  );
};

export default Profile;


