import React, { useState } from 'react';
import { data } from '../Data';

const Connection = () => {
  const [connections, setConnections] = useState(data);
  const [isconnect, setIsconnect]= useState(false);


   function clickHandler(){
    setIsconnect(true);
    console.log(isconnect);
    setIsconnect(false);
   }
  return (
    <div>
      <div className='abc container bg-primary text-white mb-5'>WELCOME TO THE ORU PHONES</div>
      <h1 className='text-center text-white bg-danger py-5' >Connections</h1>
      <ul>
        {connections.map(connection => (
          <div key={connection._id} className='card card-body bg-white xyz' style={{"width":"300px"}}><div>{connection.name} </div> <div>{connection.email}</div>
                {
                  isconnect ? <div className='btn bg-white text-success'>Remove Connection</div>:
                  <div className='btn bg-white text-success' onClick={clickHandler}>Connect</div>

                } 
                 </div>

        ))}
      </ul>
    </div>
  );
};

export default Connection;
